    func getPlayers(forCategoryName name: String) -> [Player] {
        switch name {
        case "Porteros":
            return porteros
        case "Defensas":
            return defensas
        case "Centrocampistas":
            return centrocampistas
        case "DelanteroCentro":
            return delanteroCentro
        case "Delanteros":
            return delanteros
        case "LateralIzquierdo":
            return lateralIzquierdo
        default:
            return delanteros
        }
    }
    
